jQuery(document).ready(function(c) {
    "use strict";
    c(document).on("change", ".woocommerce.widget_shopping_cart input.qty", function() {
        var t = c(this),
            a = parseInt(t.val(), 10),
            e = t.closest("li"),
            t = e.find(".remove_from_cart_button").data("cart_item_key");
        !t || isNaN(a) || a < 1 || (e.block({
            message: null,
            overlayCSS: {
                opacity: .6
            }
        }), c.ajax({
            type: "POST",
            dataType: "json",
            url: extice_cart_widget_qty_ajax.ajax_url,
            data: {
                action: "extice_change_widget_cart_qty",
                hash: t,
                quantity: a
            },
            success: function(t) {
                var a = e.closest(".widget_shopping_cart_content").find(".woocommerce-mini-cart__total .woocommerce-Price-amount.amount");
                t.data.subtotal_html && a.length && a.replaceWith(t.data.subtotal_html), t.data.product_quantity && e.find(".cart-item").html(t.data.product_quantity), c(document.body).trigger("wc_fragment_refresh")
            },
            complete: function() {
                e.unblock()
            }
        }))
    })
});